class Camisa:
    def __init__(self):
        self.__tamanho = ""

    def getTamanho(self):
        return self.__tamanho

    def setTamanho(self, valor):
        tamanhos_validos = ["PP", "P", "M", "G", "GG", "XG"]
        if valor in tamanhos_validos:
            self.__tamanho = valor
            return True
        else:
            print("Valor inválido! Os tamanhos permitidos são: PP, P, M, G, GG, XG.")
            return False

camisa = Camisa()
while camisa.getTamanho() == "": 
    print("Digite seu tamanho de camisa")
    tamanho = input() 
    camisa.setTamanho(tamanho) 
print("Parabens, você comprou uma camisa tamanho", camisa.getTamanho())
